<?php global $smof_data, $header_setings, $post;    $data_parallax = $c_pageID = null;    if($post){        $c_pageID = $post->ID;    }    /* object menu */    $menus = wp_get_nav_menus();    /* array menu id */    $menus_id = array();    if(!empty($menus)){        foreach ($menus as $menu){            $menus_id[] = $menu->term_id;        }    }    /* menu location */    $menu_locations = get_nav_menu_locations();    $main_navigation = null;    $left_navigation = null;    $right_navigation = null;    if(!empty($menu_locations) && isset($menu_locations['main_navigation'])){         $main_navigation = $menu_locations['main_navigation'];    }    if(!empty($menu_locations) && isset($menu_locations['left_navigation'])){         $left_navigation = $menu_locations['left_navigation'];    }    if(!empty($menu_locations) && isset($menu_locations['right_navigation'])){         $right_navigation = $menu_locations['right_navigation'];    }    /* custom logo */    $logo = get_post_meta($c_pageID, 'cs_header_logo_image', true);    $sticky_logo = get_post_meta($c_pageID, 'cs_sticky_header_logo_image', true);    $cs_custom_logo_sticky = get_post_meta(get_the_ID(), 'cs_custom_logo_sticky', true);     $cs_hidden_logo_page = get_post_meta(get_the_ID(), 'cs_hidden_logo_page', true);         /** data parallax */    if($smof_data['header_bg_parallax'] && !empty($smof_data['background-header']['media'])){        $data_parallax = " data-stellar-background-ratio='0.6' data-background-width='{$smof_data['background-header']['media']['width']}' data-background-height='{$smof_data['background-header']['media']['height']}'";    }    if(is_page()){        $header_full_width = get_post_meta(get_the_ID(), 'cs_header_full_width', true);        $cs_logo_width = get_post_meta(get_the_ID(), 'cs_logo_width', true);         if($header_full_width != ''){            $smof_data['header_full_width'] = $header_full_width;        }        if($cs_logo_width != ''){            $smof_data['logo_width'] = $cs_logo_width;        }    }?><div class="header header-v4">    <?php if ($smof_data['header_top_widgets'] =='1' && (is_active_sidebar('cshero-header-top-widget-1') || is_active_sidebar('cshero-header-top-widget-2'))): ?>    <div id="header-top" <?php echo ''.$header_setings->top_padding; ?>>        <div class="<?php echo ($smof_data['header_full_width'])?'no-container':'container';?>">            <div class="row">                <div class="header-top clearfix">                    <div class='header-top-1 <?php echo esc_attr($smof_data['header_top_widgets_1']); ?>'>                        <?php   if (!function_exists('dynamic_sidebar') || !dynamic_sidebar("Header Top Widget 1")):                        endif;?>                    </div>                    <?php if ($smof_data['header_top_widgets_columns'] != '1') : ?>                        <div class='header-top-2 <?php echo esc_attr($smof_data['header_top_widgets_2']); ?>'>                            <?php   if (!function_exists('dynamic_sidebar') || !dynamic_sidebar("Header Top Widget 2")):                            endif;?>                        </div>                    <?php endif; ?>                    <?php if ($smof_data['header_top_widgets_columns'] != '2') : ?>                        <div class='header-top-3 <?php echo esc_attr($smof_data['header_top_widgets_3']); ?>'>                            <?php   if (!function_exists('dynamic_sidebar') || !dynamic_sidebar("Header Top Widget 3")):                            endif;?>                        </div>                    <?php endif; ?>                </div>            </div>        </div>    </div>    <?php endif;?>    <div id="cshero-header" class="stripe-parallax-bg <?php if($cs_hidden_logo_page == '1') { ?> hidden-logo <?php } ?> <?php if (!$smof_data['header_sticky_mobile']) { echo 'no-sticky-mobile'; } ?> <?php if (!$smof_data['header_sticky_tablet']) { echo 'no-sticky-tablet'; } ?> <?php if($smof_data['header_fixed_top']){ echo ' transparentFixed';} ?>"<?php echo ''.$data_parallax; ?>>        <div class="<?php echo ($smof_data['header_full_width'])?'no-container':'container';?>">            <div class="row">                <div id="menu-left" class="main-menu-wrap">                    <div class="cs-main-menu-wrap right clearfix">                         <div class="cs_mega_menu main-menu-content cshero-menu-dropdown clearfix cshero-mobile right">                            <?php                            $megamenu = null;                            if(class_exists('HeroMenuWalker')){                                $megamenu = new HeroMenuWalker();                            }                                                        if (in_array($left_navigation, $menus_id)) {                                echo '<ul class="cshero-dropdown main-menu menu-item-padding">';                                wp_nav_menu(array('theme_location' => 'left_navigation', 'depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s', 'walker'=>$megamenu));                                echo '</ul>';                            } elseif (empty($menus_id)) {                                echo '<div class="menu-pages">';                                wp_nav_menu(array('depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s'));                                echo '</div>';                            } else {                                echo '<ul class="cshero-dropdown main-menu menu-item-padding">';                                wp_nav_menu(array('depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s', 'walker'=>$megamenu));                                echo '</ul>';                            }                            ?>                        </div>                     </div>                </div>                <div class="logo <?php if($cs_custom_logo_sticky == '1') { ?>custom-logo-sticky <?php } ?> logo-line-height-nav">                    <a class="main-logo" href="<?php echo esc_url(home_url()); ?>">                        <img src="<?php echo esc_url($logo ? wp_get_attachment_url($logo) : $smof_data['logo']['url']); ?>" alt="<?php esc_attr(bloginfo('name')); ?>"                             style="max-height: <?php echo esc_attr($smof_data["logo_width"]); ?>" class="normal-logo logo-v1"/>                    </a>                    <?php if($cs_custom_logo_sticky == '1') { ?>                        <a class="sticky-logo" href="<?php echo esc_url(home_url()); ?>">                            <img src="<?php echo wp_get_attachment_url($sticky_logo); ?>"                                 style="max-height: <?php echo esc_attr($smof_data["logo_width"]); ?>" class="normal-logo logo-v1"/>                        </a>                    <?php } ?>                </div>                <div id="menu-right" class="main-menu-wrap">                    <div class="cs-main-menu-wrap left clearfix">                         <div class="cshero-header-content-widget cshero-menu-mobile hidden-lg hidden-md right">                            <div class="cshero-header-content-widget-inner">                                <a class="btn-navbar" data-toggle="collapse" data-target="#cshero-main-menu-mobile" href="#" ><i class=""></i></a>                            </div>                        </div>                        <div class="cs_mega_menu main-menu-content cshero-menu-dropdown clearfix cshero-mobile right">                            <?php                            $megamenu = null;                            if(class_exists('HeroMenuWalker')){                                $megamenu = new HeroMenuWalker();                            }                                                        if (in_array($right_navigation, $menus_id)) {                                echo '<ul class="cshero-dropdown main-menu menu-item-padding">';                                wp_nav_menu(array('theme_location' => 'right_navigation', 'depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s', 'walker'=>$megamenu));                                echo '</ul>';                            } elseif (empty($menus_id)) {                                echo '<div class="menu-pages">';                                wp_nav_menu(array('depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s'));                                echo '</div>';                            } else {                                echo '<ul class="cshero-dropdown main-menu menu-item-padding">';                                wp_nav_menu(array('depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s', 'walker'=>$megamenu));                                echo '</ul>';                            }                            ?>                        </div>                     </div>                </div>                <div id="cshero-main-menu-mobile" class="collapse navbar-collapse cshero-mmenu">                    <?php                        if (in_array($left_navigation, $menus_id)) {                            echo '<ul class="cshero-dropdown cshero-mobile-menu">';                            wp_nav_menu(array('theme_location' => 'left_navigation', 'depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s', 'walker'=>$megamenu));                            wp_nav_menu(array('theme_location' => 'right_navigation', 'depth' => 5, 'container' => false, 'menu_id' => 'nav', 'items_wrap' => '%3$s', 'walker'=>$megamenu));                            echo '</ul>';                        }                    ?>                </div>            </div>        </div>        <svg xmlns="http://www.w3.org/2000/svg" width="100%" viewBox="0 0 100 100" version="1.1" preserveAspectRatio="none" height="45px" class="decor">          <path stroke-width="0" d="M0 100 C50 0 50 0 100 100 Z"/>        </svg>    </div></div>